              type="date"
              id="fechaInicio"
              className="form-control"
              onChange={e => setFechaInicio(e.target.value)}
            />
          </div>

          <div classNanme="col-md-3">
            <label for="cantiAM">Cantidad Complemento AM :</label>
            <input
              type="number"
