import React from 'react'
import Form from "react-validation/build/form";
import CheckButton from "react-validation/build/button";
import Input from "react-validation/build/input";
import axios from 'axios';
import { Modal, Button, } from 'react-bootstrap'

class Modales extends React.Component {

    handleEnviar = e => {
        e.preventDefault();
        const datos = {
            id: 1,
            username: this.state.username,
            email: this.state.email,
            password: this.state.password
        };
        axios.put('http://localhost:8080/api/usuarios/actualizar/1', datos)
            .then(res => {
                console.log(res)
            })
            .catch(
                err => {
                    console.log(err);
                    alert('Incorrecto')
                }
            )
    }

    constructor() {
        super()
        this.state = {
            showModal: false
        }
    }

    handleModal() {
        this.setState({ showModal: !this.state.showModal })
    }

    render() {
        return (
            <div>
                <Button onClick={() => this.handleModal()}>Abrir Modal</Button>
                <div>
                        <Modal show={this.state.showModal} onHide={() => this.handleModal()} >
                            <Modal.Header closeButton>
                                Actualizar Contraseña, Usuario o Email
                            </Modal.Header>
                            <Modal.Body>
                                <Form onSubmit={this.handleEnviar} ref={h => { this.form = h; }}>
                                    <div>
                                        <label>Username</label>
                                        <Input
                                            onChange={i => this.username = i.target.value}
                                            value={this.state.setusername}
                                        />
                                    </div>
                                    <div>
                                        <label>Email</label>
                                        <Input
                                            onChange={i => this.email = i.target.value}
                                            value={this.state.setemail}
                                        />
                                    </div>
                                    <div>
                                        <label>Contraseña</label>
                                        <Input
                                            onChange={i => this.password = i.target.value}
                                            value={this.state.setid}
                                        />
                                    </div>
                                </Form>
                            </Modal.Body>
                            <Modal.Footer>
                                <CheckButton ref={h => { this.checkBtn = h; }} >Actualizar</CheckButton>
                                <Button onClick={() => this.handleModal()} variant="danger" >Cancelar</Button>
                            </Modal.Footer>
                        </Modal>
                    </div>
            </div>
        );
    }
}
export default Modales;
