import axios from 'axios';
import React, { Component } from 'react'

export default class contenido_selects extends Component {

    handleSubmit = e => {
        e.preventDefault();
         this.unDepartamento = parseInt(this.unDepartamento);
         this.unMunicipio = parseInt(this.unMunicipio);
        const data = {
            unMunicipio: this.unMunicipio,
            unDepartamento: this.unDepartamento,
        };
        axios.post('http://localhost:8080/api/estudiante', data).then(
            res => {
                console.log(res)
                console.log(data)
            }
        ).catch(
            err => {
                console.log(err);
                console.log(data)
            }
        )
    };


    state = {
        municipio: [],
        departamento: []

    }

    componentDidMount() {
        axios
            .get('http://localhost:8080/api/municipios/listar')
            .then((response) => {
                console.log(response);
                this.setState({ municipio: response.data })
            })
            .catch((error) => {
                console.log(error);
            });

        axios
            .get('http://localhost:8080/api/departamento/listar')
            .then((response) => {
                console.log(response);
                this.setState({ departamento: response.data })
            })
            .catch((error) => {
                console.log(error);
            });
    }



    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Municipios</label>
                    <select className="form-select" aria-label="Default select example" onChange={e => this.unMunicipio = e.target.value}
                        defaultValue={{ label: 'Selecciona una Opción', value: 'empty' }}>
                        {this.state.municipio.map(municipios => (
                            <option key={municipios.idMunicipio} value={{ "idMunicipio": municipios.idMunicipio, "codigo": municipios.codigo, "nombre": municipios.nombre }}>{municipios.nombre}</option>
                        )
                        )}
                    </select>
                    <label>Departamentos</label>
                    <select className="form-select" aria-label="Default select example" onChange={e => this.unDepartamento = e.target.value}>
                        {this.state.departamento.map(departamentos => (
                            <option key={departamentos.idDepartamento} value={departamentos.idDepartamento + "," + departamentos.nombre + "," + departamentos.codigo}>{departamentos.nombre}</option>
                        )
                        )}
                    </select>
                    <button >Enviar</button>
                </form>
            </div >
        )
    }
}
