import { Link } from "react-router-dom";
import { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button"

import AuthService from "../services/auth.service";

import './Contenido_Login.css'
import '../pages/Inicio'


const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        this field is required!
      </div>
    );
  }
};






export default class Contenido_Login extends Component {

  constructor(props) {
    super(props);
    this.handlelogin = this.handlelogin.bind(this);
    this.onChangeusernameOrEmail = this.onChangeusernameOrEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);

    this.state = {
      usernameOrEmail: "",
      password: "",
      loading: false,
      message: ""
    };
  }

  onChangeusernameOrEmail(e) {
    this.setState({
      usernameOrEmail: e.target.value
    });
  }


  onChangePassword(e) {
    this.setState({
      password: e.target.value
    });
  }


  handlelogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.login(this.state.usernameOrEmail, this.state.password).then(
        () => {
          this.props.history.push("/Buscar_Estudiante");
          window.location.reload();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            loading: false,
            message: resMessage
          });
        }
      );
    } else {
      this.setState({
        loading: false
      });
    }
  }



  render() {
    return (
      <>
        <div id="login_div_bienvenido">
          <h1 className="text-center" id='login_tex1'>Bienvenido</h1>
          <h2 className="text-center" id='login_tex2'>Fundación Mía</h2>
          <h3 className="text-center" id='login_tex3'>Construyendo Futuro</h3>
          <div id='login_imag'> <img src='/img/usuario50.jpg' alt='' /> </div>
        </div>
        <br />

        <Form 
          onSubmit={this.handlelogin}
          ref={c => {
            this.form = c;
          }}
        >
          <div className="form-group">
            <label htmlFor="usernameOrEmail">Username</label>
            <Input
              type="text"
              className="form-control"
              name="usernameOrEmail"
              value={this.state.usernameOrEmail}
              onChange={this.onChangeusernameOrEmail}
              validations={[required]}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <Input
              type="text"
              className="form-control"
              name="password"
              value={this.state.password}
              onChange={this.onChangePassword}
              validations={[required]}
            />
          </div>

          <div className="form-group">
            <button
              className="btn btn-primary btn-block"
              disabled={this.state.loading}
            >
              {this.state.loading && (
                <span className="spinner-border spinner-border-sm"></span>
              )}
              <span>Ingresar</span>
            </button>
          </div>

          {this.state.message && (
            <div className="form-group">
              <div className="alert alert-danger" role="alert">
                {this.state.message}
              </div>
            </div>
          )}
          <CheckButton
            style={{ display: "none" }}
            ref={c => {
              this.checkBtn = c;
            }}
          />
        </Form>

        {/*--------------------------------------------------------- 
        <form id="login_usform">
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">Usuario</label>
            <input type="text"
              className="form-control"
              id="login_exampleInputEmail1"
              aria-describedby="emailHelp"
              placeholder="Nombre de Usuario"
            />
          </div>
          <br></br>
          <form className="form-group">
            <label htmlFor="exampleInputPassword1">Contraseña</label>
            <input type="password"
              className="form-control"
              id="login_exampleInputPassword1"
              placeholder="Contraseña"
            />
          </form>
          <br />
          <div id="login_div_links">
            <Link id="login_link_res" to="/Login">He Olvidado Mi Contraseña</Link>
            <div className='btn btn-danger'><Link id="login_link" to="/Inicio">Ingresar</Link></div>
          </div>
        </form>*/}
      </>
    );
  }
}
