import axios from 'axios';
import React, { Component } from 'react'

export default class contenido_selects extends Component {

    statedocumento = {
        documento: [],
        documentoTipo: ''
    }
    componentDidMount() {
        this.getdocumento();
        this.getdepartamento();
    }
    getdocumento = () => {
        axios.get('http://localhost:8080/api/municipios/listar')
            .then(res => {
                this.setState({
                    documento: res.data,
                });
                console.log(this.state);
            });
    }
    handleChange = (e) => {
        this.setState({
            documentoTipo: e.target.value
        })
    }

    statedepartamento = {
        departamento: [],
        departamentoTipo: ''
    }
    //componentDidMount(){
   //     this.getdepartamento();
   // }




    render() {
        return (
            <div>
                <label>Tipo Documento</label>
                <select className="form-select" aria-label="Default select example" onChange={this.handleChange}>
                    {this.state.documento.map((documentos) => {
                        return (
                            <option key={documentos.id_municipio}
                                value={documentos.nombre}>{documentos.nombre}</option>
                        )
                    })
                    }
                </select>
            </div>
        )
    }
}
