const API_URL = 'http://localhost:8080/api'


export const listEstudiantes = async () => {
    return await fetch(API_URL + "/estudiante");
}

export const crearEstu = async (newEstudiante) => {
    return await fetch(API_URL + "crear/", {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            "nombre": String(newEmpresa.nombre).trim(),
            "direccion": String(newEmpresa.direccion).trim(),
            "nit": String(newEmpresa.nit).trim(),
            "telefono": parseInt(newEmpresa.telefono),
        })
    })
}

export const deleteEmpresa = async (empresaId) => {
    return await fetch(`${API_URL + "eliminar/"}${empresaId}/`, {
        method: 'DELETE'

    });
}

export const updateEmpresa = async (empresaId, newEmpresa) => {
    return await fetch(`${API_URL + "update/"}${empresaId}/`, {
        method: 'PUT',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            "nombre": String(newEmpresa.nombre).trim(),
            "direccion": String(newEmpresa.direccion).trim(),
            "nit": String(newEmpresa.nit).trim(),
            "telefono": parseInt(newEmpresa.telefono),
        })
    });
}
