import { Component } from 'react';
import axios from 'axios';

class ListEstudiantes extends Component {

    state = {
        documentos: [],
        documentosTipo: ''
    }
    componentDidMount() {
        this.getdocumentos();
    }

    getdocumento = () => {
        axios.get('http://localhost:8080/api/tipoDocumento')
            .then(res => {
                this.setState({
                    documento: res.data,
                });
                console.log(this.state);
            });
    }
    handleChange = (e) => {
        this.setState({
            documentoTipo: e.target.value
        })
    }

    render() {
        return (
            <form className="form-group">
                <div id="PathFindes-box" className='form-group'>
                    <label>Path Findees:
                        <select className="form-select center" aria-label="Default select example" onChange={this.handleChange}>
                            {/*Listado de documento*/}
                            {this.state.documento.map((PathFindee) => {
                                return (
                                    <option key={PathFindee.idTipoDocumento} value={PathFindee.nombre}>{PathFindee.nombre}</option>
                                )
                            })
                            }
                        </select>
                    </label>
                    <div id="Role_Date-box">
                        <label>{this.state.documentoTipo}</label>
                    </div>
                </div>
            </form>
        );
    }
}
export default ListEstudiantes
