import  { useEffect, useRef, useState} from 'react'
import './contenidoBeneficioAdmin.css'
import { Link } from "react-router-dom";
import axios from "axios";
import  env from "../../env.json";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";

const ContenidoBeneficioAdmin = () => {
  const formRef = useRef();

  const [ roles, setRol ] = useState([]);
  const [ unTipoDocumento, setTipoDocumento ] = useState([]);

  const obtenerRol= async () => {
    try {
      const response = await axios.get(`${env.host}/api/roles`);	
      setRol(response.data);
    } catch (err) {
      console.log(err);
    }
  };

  const obtenerTipoDocumento= async () => {
    try {
      const response = await axios.get(`${env.host}/api/tipo_documento`);	
      setTipoDocumento(response.data);
      } catch (err) {
      console.log(err);
      }
    };

  return (
    <div id='beneficio_div'>
      <div id='beneficio_divruta'>
        <img id="beneficio_iconos" src='/img/icono_inicio.png' alt='' />
        <Link id="beneficio_linkinicio" to="/InicioAdmin">Inicio/</Link>
        <img id="beneficio_iconos" src='/img/icono_estudiantes.png' alt='' />
        <label id='beneficio_textrutas'>Beneficio/</label>
        <label id='beneficio_textrutas'>Registro Beneficio</label>
      </div>
      <Form id="beneficio_form">
        <div>
          <label>Username:</label>
          <Input
          />
        </div>
        <div>
          <label>Email:</label>
          <Input
          />
        </div>
        <div>
          <label>Rol:</label>
          <select
            className="from_select"
            aria-label='default select example'
            id="huella_inputs"
            onChange={e => setRol(e.target.value)}
          >
            <option value="">Seleccione un rol</option>
            {roles && roles.map(rol =>
              <option key={rol.id} value={rol.id}>{rol.nombre}</option> 
            )} 
          </select>      
        </div>
        <div>
          <label>Tipo Documento:</label>
          <Input
          />
        </div>
        <div>
          <label>Numero Documento:</label>
          <Input
          />
        </div>
        <div>
          <label>Telefono:</label>
          <Input
          />
        </div>
        <div>
          <label>Nombre:</label>
          <Input
          />
        </div>
        <div>
          <label>Apellido:</label>
          <Input
          />
        </div>
        <div>
          <label>Fecha Nacimiento:</label>
          <Input
            type="date"
          />
        </div>
        <div>
          <label>Lugar Nacimiento:</label>
          <Input
          />
        </div>
        <div>
          <label>Direcci√≥n:</label>
          <Input
          />
        </div>
        <div>
          <label>Barrio:</label>
          <Input
          />
        </div>
      </Form>
    </div>
  );
}

export default ContenidoBeneficioAdmin;
