import axios from 'axios';
import React, { Component } from 'react'

export default class contenido_selects extends Component {

    handleSubmit = e => {
        e.preventDefault();
        this.unaSede = parseInt(this.unaSede);

        const data = [{
            unaSede: this.unaSede
        }];
        axios.post('http://localhost:8080/api/estudiante', data).then(
            res => {
                console.log(res)
                console.log(data)
            }
        ).catch(
            err => {
                console.log(err);
                console.log(data)
            }
        )
    };


    state = {
        documento: [],
        departamento: []
    }
    componentDidMount() {
        axios
            .get('http://localhost:8080/api/municipios/listar')
            .then((response) => {
                console.log(response);
                this.setState({ documento: response.data })
            })
            .catch((error) => {
                console.log(error);
            });

        axios
            .get('http://localhost:8080/api/departamento/listar')
            .then((response) => {
                console.log(response);
                this.setState({ departamento: response.data })
            })
            .catch((error) => {
                console.log(error);
            });
    }

    render() {
        return (
            <div>
                <label>Municipios</label>
                <select className="form-select" aria-label="Default select example" onChange={this.handleChange} >
                    {this.state.documento.map(documentos => (
                        <option key={documentos.idMunicipio} value={documentos.idMunicipio}>{documentos.nombre}</option>
                    )
                    )}
                </select>
                <label>Departamentos</label>
                <select className="form-select" aria-label="Default select example">
                    {this.state.departamento.map(departamentos => (
                        <option key={departamentos.idDepartamento} value={departamentos.idDepartamento}>{departamentos.nombre}</option>
                    )
                    )}
                </select>
                <button onChange={handle}>Enviar</button>
            </div>
        )
    }
}
