import React from 'react'
import Form from "react-validation/build/form";
import CheckButton from "react-validation/build/button";
import Input from "react-validation/build/input";
import axios from 'axios';
import { Modal, Button, } from 'react-bootstrap'

class Modales extends React.Component {

    handleEnviar = e => {
        e.preventDefault();
        const data = {
            id: 1,
            username: this.state.username,
            email: this.state.email,
            password: this.state.password
        };
        axios.put('http://localhost:8080/api/usuarios/actualizar/1', data)
            .then(res => {
                console.log(res)
            })
            .catch(
                err => {
                    console.log(err);
                    alert('Incorrecto')
                }
            )
    }

    constructor() {
        super()
        this.state = {
            showModal: false
        }
    }

    handleModal() {
        this.setState({ showModal: !this.state.showModal })
    }

    render() {
        return (
            <div>
                <Button onClick={() => this.handleModal()}>Abrir Modal</Button>
                <Modal show={this.state.showModal} onHide={() => this.handleModal()} >
                    <Modal.Header closeButton>
                        Actualizar Contraseña, Usuario o Email
                    </Modal.Header>
                    <Modal.Body>
                        <Form onSubmit={this.handleEnviar} ref={c => { this.form = c; }}>
                            <div>
                                <label>Username</label>
                                <Input
                                    onChange={e => this.username = e.target.value}
                                    value={this.state.setusername}
                                />
                            </div>
                            <div>
                                <label>Email</label>
                                <Input
                                    onChange={e => this.email = e.target.value}
                                    value={this.state.setemail}
                                />
                            </div>
                            <div>
                                <label>Contraseña</label>
                                <Input
                                    onChange={e => this.password = e.target.value}
                                    value={this.state.setid}
                                />
                            </div>
                        </Form>
                    </Modal.Body>
                    <Modal.Footer>
                         <CButton ref={c => { this.checkBtn = c; }} >Actualizar</CButton>
                        <Button onClick={() => this.handleModal()} variant="danger" >Cancelar</Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}
export default Modales;
