const API_URL = "http://localhost:8000//api/estudiante";

export const listEstudiantes = async () => {
    return await fetch(API_URL + "estudiante/");
}


export const crearEstudiante = async (newEstudiante) => {
    return await fetch(API_URL + "create/", {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            "minicipio": String(newEstudiante.municipio).trim(),
            "institucion": String(newEstudiante.institucion).trim(),
            "sede": String(newEstudiante.sede).trim(),
            "fechaRegistro": String(newEstudiante.fechaRegistro).trim(),
            "tipoDocumento": String(newEstudiante.tipoDocumento).trim(),
            "numeroDocumento": String(newEstudiante.numeroDocumento).trim(),
            "nombre1": String(newEstudiante.nombre1).trim(),
            "apellido1": String(newEstudiante.apellido1).trim(), 
            "grado": String(newEstudiante.grado).trim(),
            "direccion": String(newEstudiante.direccion).trim(),
            "barrio": String(newEstudiante.barrio).trim(),
            "fechaNacimiento": String(newEstudiante.fechaNacimiento).trim(),
            "acudiente": String(newEstudiante.acudiente).trim(),
            "parentesco": String(newEstudiante.parentesco).trim(),
            "telefono": String(newEstudiante.telefono).trim()
        })
    })
}
